export const drawRect = (boxes, isFace, isEngaged, imgWidth, imgHeight, ctx)=>{
    boxes[0] = boxes[0] * imgWidth
    boxes[1] = boxes[1] * imgHeight
    boxes[2] = boxes[2] * imgWidth
    boxes[3] = boxes[3] * imgHeight

    if(isFace[0] > 0.7){
        const [x1, y1, x2, y2] = boxes
        
        if (isEngaged[0] > 0.9) {
            ctx.strokeStyle = 'green'
            ctx.lineWidth = 10
            ctx.fillStyle = 'white'
            ctx.font = '30px Arial'         
            
            ctx.beginPath()
            ctx.fillText('Engaged: ' + Math.round(isEngaged[0] * 100) / 100, x1, y1 - 10)
            ctx.rect(x1, y1, (x2 - x1), (y2 - y1));
            ctx.stroke()
        } else {
            ctx.strokeStyle = 'red'
            ctx.lineWidth = 10
            ctx.fillStyle = 'white'
            ctx.font = '30px Arial'         
            
            ctx.beginPath()
            ctx.fillText('Not Engaged: ' + Math.round((1 - isEngaged[0]) * 100) / 100, x1, y1 - 10)
            ctx.rect(x1, y1, (x2 - x1), (y2 - y1));
            ctx.stroke()
        }
    }
}
