import React, { useRef, useEffect, useState } from "react";
import '../css/App.css';
import * as tf from "@tensorflow/tfjs";
import { drawRect } from "../util/utilities"; 
import EngagementGraph from './EngagementGraph';

import Webcam from "react-webcam";

function App() {
  const [data, setData] = useState([]);
  var engagement_value = 0

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);

  const updateEngagementLevel = (engagement_level) => {
    engagement_value = engagement_value + engagement_level
    setData(prevData => [...prevData, {'name': 'Engagement', 'engagement_level': engagement_value}]);
  }

  // ---------------------------- //

  const runObjectDetection = async () => {
    const net = await tf.loadLayersModel('https://s3.amazonaws.com/sachinreddy.com/model/model.json')

    const intervalId = setInterval(() => {
      detect(net);
    }, 150);

    return () => clearInterval(intervalId);
  };

  const detect = async (net) => {
    if (
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;

      const img = tf.browser.fromPixels(video)
      const resized = tf.image.resizeBilinear(img, [120,120])
      const normalized = resized.div(tf.scalar(255.0))

      const casted = normalized.cast('float32')
      const expanded = casted.expandDims(0)

      const obj = await net.predict(expanded)

      const isFace = await obj[0].array()
      const isEngaged = await obj[1].array()
      const boxes = await obj[2].array()

      const ctx = canvasRef.current.getContext("2d");

      requestAnimationFrame(
        () => {
          drawRect(boxes[0], isFace[0], isEngaged[0], videoWidth, videoHeight, ctx, updateEngagementLevel)
        }
      ); 

      tf.dispose(img)
      tf.dispose(resized)
      tf.dispose(normalized)
      tf.dispose(casted)
      tf.dispose(expanded)
      tf.dispose(obj)
    }
  };

  useEffect(() => {
    runObjectDetection()
  }, []);

  const videoConstraints = {
    facingMode: "user"
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="row">
            <Webcam
              ref={webcamRef}
              muted={true} 
              videoConstraints={videoConstraints}
              className="video_container"
            />

            <canvas
              ref={canvasRef}
              className="video_container stack-top"
            />
        </div>

        <div className="row">
          <EngagementGraph
            data={data}
          />
        </div>
      </header>
    </div>
  );
}

export default App;
